var open=document.querySelector(".btn-contacts"),popup=document.querySelector(".modal-window"),close=popup.querySelector(".modal-window-close"),focus=popup.querySelector("[name=name]");open.addEventListener("click",function(a){a.preventDefault(),popup.classList.add("modal-window-show"),focus.focus()}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("modal-window-show")}),window.addEventListener("keydown",function(a){27===a.keyCode&&popup.classList.contains("modal-window-show")&&popup.classList.remove("modal-window-show")});
